    private void JBtnDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_JBtnDeleteActionPerformed
        // TODO add your handling code here:
        
        String nextNum = JTextResult.getText();
        char[] resultLength = nextNum.toCharArray();
        int digitLength = resultLength.length;
        
        if(digitLength==1){
            JTextResult.setText("0");
            JLabelLength.setText("0");
        }else{
            digitLength--;
            String newDigitLength = nextNum.substring(0, digitLength);
            JTextResult.setText(newDigitLength);
        }
        
    }//GEN-LAST:event_JBtnDeleteActionPerformed

