    private void JBtnEqualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_JBtnEqualActionPerformed
        // TODO add your handling code here:
        String snum = JTextResult.getText();
        int snum1 = Integer.parseInt(snum);
        String fnum = JLabelFnum.getText();
        int fnum1 = Integer.parseInt(fnum);

        String Operation = JLabelOperation.getText();

        switch (Operation) {
            case "+":
            String floatActive = JLabelFloat.getText();
            String sum1;
				if(floatActive.equals("true")){
					float sum = fnum1 + snum1;
					sum1 = String.valueOf(sum);
				}else{
					int sum = fnum1 + snum1;
					sum1 = String.valueOf(sum);
				}
				
				int digitLength = sum1.length();
				if(digitLength<=8){
					//String sumResultLength = sum1.substring(1,8);
					JTextResult.setText(sum1);
					JLabelFnum.setText(sum1);
					JLabelOperation.setText("None");
					JLabelLength.setText("0");
				}else{
					String sumResultLength = sum1.substring(1,8);
					JTextResult.setText(sumResultLength);
					JLabelFnum.setText(sumResultLength);
					JLabelOperation.setText("None");
					JLabelLength.setText("0");
				}
				break;
            
            case "-":
				floatActive = JLabelFloat.getText();
				String diff1;
				if(floatActive.equals("true")){
					float diff = fnum1 + snum1;
					diff1 = String.valueOf(diff);
				}else{    
					int diff = fnum1 - snum1;
					diff1 = String.valueOf(diff);
				}
				
				digitLength = diff1.length();
				if(digitLength<=8){
					JTextResult.setText(diff1);
					JLabelFnum.setText(diff1);
					JLabelOperation.setText("None");
					JLabelLength.setText("0");
				}else{
					String diffResultLength = diff1.substring(1,8);
					JTextResult.setText(diffResultLength);
					JLabelFnum.setText(diffResultLength);
					JLabelOperation.setText("None");
					JLabelLength.setText("0");
				}
				break;
            
            case "*":
				int prod = fnum1 * snum1;
				String prod1 = String.valueOf(prod);
				digitLength = prod1.length();
				if(digitLength<=8){
					JTextResult.setText(prod1);
					JLabelFnum.setText(prod1);
					JLabelOperation.setText("None");
					JLabelLength.setText("0");
				}else{
					String prodResultLength = prod1.substring(1,8);
					JTextResult.setText(prodResultLength);
					JLabelFnum.setText(prodResultLength);
					JLabelOperation.setText("None");
					JLabelLength.setText("0");
				}
            break;
            
            case "/":
				int qout = fnum1 / snum1;
				String qout1 = String.valueOf(qout);
				digitLength = qout1.length();
				if(digitLength<=8){
					int remainder = fnum1%snum1;
					String remainder1 = String.valueOf(remainder);
					if(remainder==0){
						JTextResult.setText(qout1);
						JLabelFnum.setText(qout1);
					}else{
						JTextResult.setText(qout1 + "." + remainder1);
						JLabelFnum.setText(qout1 + "." + remainder1);
					}
					JLabelOperation.setText("None");
					JLabelLength.setText("0");
				}else{
					String qoutResultLength = qout1.substring(1,8);
					JTextResult.setText(qoutResultLength);
					JLabelFnum.setText(qoutResultLength);
					JLabelOperation.setText("None");
					JLabelLength.setText("0");
				}
				break;
            
            default:
                break;
        }
    }//GEN-LAST:event_JBtnEqualActionPerformed

